import { HYDRATE } from "next-redux-wrapper"

const initialState ={
    user:{
        isLoggedIn:false,
        user:null,
        sighUpData:{},
        loginData :{},
    },
    post:{
        mainPosts:[],
    }
    
}

export const loginAction= (data)=>{
    return{
        type:'LOG_IN',
        data,
    }
}
export const logoutAction= (data)=>{
    return{
        type:'LOG_OUT',
        data,
    }
}
const changeNickname = (data) =>{
    return {
        type:'CHANGE_NICKNAME',
        data,
    }
}

changeNickname('neue zeal')


//(이전상태, 액션) => 다음상태
const rootReducer = (state=initialState,action)=>{
    switch(action.type){

        case HYDRATE:
            console.log('HYDRATE', action);
            return {
                ...state, ...action.payload
            }
        case 'LOG_IN':
            return{
                ...state,
                user:{
                ...state.user,
                isLoggedIn:true,
                uesr:action.data,
                }
                    }
        case 'LOG_OUT':
             return{
                 ...state,
                user:{
                ...state.user,
                isLoggedIn:false,
                uesr:null,
                 }
                                        }
                     
        default:
            return state;

     }
}

export default rootReducer;
