const initialState ={
    user:{
        istLoggedIn:false,
        user:pull,
        sighUpData:{},
        loginData :{},
    },
    post:{
        mainPosts:[],
    }
    
}

const login = (data)=>{
    return{
        type:'LOG_IN',
        data,
    }
}
const changeNickname = (data) =>{
    return {
        type:'CHANGE_NICKNAME',
        data ,

    }
}

changeNickname('neue zeal')

store.dispatch(changeNickname('mighty back'))

//(이전상태, 액션) => 다음상태
const rootReducer = (state=initialState,action)=>{
    switch(action.type){
        case 'LOG_IN':
            return{
                ...state,
                user:{
                ...state.user,
                istLoggedIn:true,
                uesr:action.data,
                }



                            }

     }
}

export default rootReducer;
